<h1>Users</h1><?php $canEdit = $currentUser->hasAccessTo('lhuser','edituser'); $canDelete = $currentUser->hasAccessTo('lhuser','deleteuser'); ?><table class="table" cellpadding="0" cellspacing="0" width="100%"><thead><tr><th width="1%">ID</th><th>Username</th><th>E-mail</th><th>Last activity</th><?php  if ($canEdit) : ?><th width="1%">&nbsp;</th><?php  endif;?><?php  if ($canDelete) : ?><th width="1%">&nbsp;</th><?php  endif;?></tr></thead><?php  foreach ($userlist as $user) : ?><tr><td><?php  echo $user->id?></td><td><?php  echo htmlspecialchars($user->username)?></td><td><?php  echo htmlspecialchars($user->email)?></td><td><?php  echo $user->lastactivity_ago?> ago</td><?php  if ($canEdit) : ?><td><a class="btn btn-default btn-xs" href="/index.php/site_admin/user/edit/<?php  echo $user->id?>">Edit</a></td><?php  endif;?><?php  if ($canDelete) : ?><td><?php  if ($user->id != 1) : ?><a class="btn btn-danger btn-xs csfr-required" onclick="return confirm('Are you sure?')" href="/index.php/site_admin/user/delete/<?php  echo $user->id?>">Delete</a><?php  endif;?></td><?php  endif;?></tr><?php  endforeach; ?></table><script>lhinst.protectCSFR();</script><?php  if (isset($pages)) : ?><?php  if (isset($pages) && $pages->num_pages > 1) : ?><nav><ul class="pagination paginator-lhc"><?php  if ($pages->current_page != 1) : ?><li class="arrow"><a class="previous" href="<?php  echo $pages->serverURL,$pages->prev_page,$pages->querystring?>">&laquo;</a></li><?php  endif;?><?php  if ($pages->num_pages > 10) : $needNoBolder = false; if ($pages->range[0] > 1) : $i = 1; $pageURL = $i > 1 ? '/(page)/'.$i : ''; $needNoBolder = true; if ($i == $pages->current_page) : ?><li class="current no-b"><a title="Go to page <?php  echo $i?> of <?php  echo $pages->num_pages?>" href="#"><?php  echo $i?></a></li><?php  else : ?><li><a class="no-b" title="Go to page <?php  echo $i?> of <?php  echo $pages->num_pages?>" href="<?php  echo $pages->serverURL,$pageURL,$pages->querystring?>"><?php  echo $i?></a></li><?php  endif; ?><li><a href="#">...</a></li><?php endif; for($i=$pages->range[0];$i<=$pages->lastArrayNumber;$i++) : if ($i > 0) : $pageURL = $i > 1 ? '/(page)/'.$i : ''; $noBolderClass = ($i == 1 || $needNoBolder == true) ? ' no-b' : ''; $needNoBolder = false; if ($i == $pages->current_page): ?><li class="active<?php  echo $noBolderClass?>"><a title="Go to page <?php  echo $i?> of <?php  echo $pages->num_pages?>"  href="#"><?php  echo $i?></a></li><?php  else : ?><li><a class="<?php  echo $noBolderClass?>" title="Go to page <?php  echo $i?> of <?php  echo $pages->num_pages?>" href="<?php  echo $pages->serverURL,$pageURL,$pages->querystring?>"><?php  echo $i?></a></li><?php  endif;endif;endfor; if ($pages->lastArrayNumber < $pages->num_pages) : $i = $pages->num_pages; $pageURL = $i > 1 ? '/(page)/'.$i : ''; ?><li><a href="#">...</a></li><?php  if ($i == $pages->current_page) : ?><li class="active"><a title="Go to page <?php  echo $i?> of <?php  echo $pages->num_pages?>" href="#"><?php  echo $i?></a></li><?php   else : ?><li><a class="no-b" title="Go to page <?php  echo $i?> of <?php  echo $pages->num_pages?>" href="<?php  echo $pages->serverURL,$pageURL,$pages->querystring?>"><?php  echo $i?></a></li><?php  endif; endif; else : for ($i=1;$i<=$pages->num_pages;$i++) : $noBolderClass = ($i == 1) ? ' no-b' : ''; $pageURL = $i > 1 ? '/(page)/'.$i : ''; if ($i == $pages->current_page) :?><li class="active<?php  echo $noBolderClass?>"><a href="#"><?php  echo $i?></a></li><?php  else : ?><li><a class="paginate" href="<?php  echo $pages->serverURL,$pageURL,$pages->querystring;?>"><?php  echo $i?></a></li><?php  endif; endfor; endif; if ($pages->current_page != $pages->num_pages): ?><li class="arrow"><a class="next" href="<?php  echo $pages->serverURL,'/(page)/',$pages->next_page,$pages->querystring?>">&raquo;</a></li><?php  endif;?></ul><div class="found-total pull-right">Page <?php  echo $pages->current_page?> of <?php  echo $pages->num_pages?>, Found - <?php  echo $pages->items_total?></div></nav><?php  endif;?><?php  endif;?><br /><?php  if ($currentUser->hasAccessTo('lhuser','createuser')) : ?><div><a class="btn btn-default" href="/index.php/site_admin/user/new">New user</a></div><?php  endif; ?>